import React, { Component } from 'react';
import Navegacion from './Navegacion';
import NavegacionAdmin from './VistaAdmin/NavegacionAdmin';
import NavegacionSuscriptor from './VistaSuscriptor/NavegacionSuscriptor'
const soyAdminApi = 'http://localhost:4000/api/suscriptores/soyAdmin'

class NavegacionMaster extends Component {
    constructor(){
        super();
        this.state={
            soyAdmin:false
        }
        this.setSoyAdmin();
    }
    componentDidMount(){
        this.setSoyAdmin();
    }
    setSoyAdmin= async()=> {
        await axios.get( soyAdminApi ,{
            headers:{'xaccess':this.state.token}  
        }).then(res =>{
            this.setState({
                soyAdmin : res.data
            });
        })
    };



    render() {
        
        if(sessionStorage.getItem('token') == null){
            return (
                <Navegacion></Navegacion>
            )
        }
       
        
        if(this.state.soyAdmin){
            return (
                <NavegacionAdmin></NavegacionAdmin>
            )
        }else{
            return (
                <NavegacionSuscriptor></NavegacionSuscriptor>
            )
        }
     
       

    
        
    }
}
export default NavegacionMaster;
