import React, { Component } from 'react';

import axios from 'axios';
import ItemNovedad from './ItemNovedad';
import ItemListNovedad from './ItemListNovedad';



const novedades= 'http://localhost:4000/api/novedades/';
export default class ListarNovedades extends Component {
    constructor(){
        super();
        this.state={
            user: JSON.parse(sessionStorage.getItem('user')),
            token: sessionStorage.getItem('token'),
            novedades:[],
            nove: true,
            novedadAct:null
        }

    }
    setNovedades(res){
        console.log(res);
        this.setState({
            novedades:res
        });
    }

    getData = async () =>{
        const {user} = this.state.user;
        
      
        await axios.get(novedades,{
            user: user,
            headers:{'xaccess':this.state.token}  
        })
        .then(res =>{
            this.setNovedades(res.data)
        })
        .catch(err =>{
            console.log('lista de novedades');
            console.log(err.response)});

    }
    async componentDidMount(){

        this.getData();
    }
    cambiarNove=()=>{
        this.setState({
            nove:!this.state.nove  
        })
    }

    setNovedad=(nov)=>{
        this.setState({
            novedadAct:nov
        })
        this.cambiarNove();
    }

    render() {

        if(this.state.nove){
            return (
                <div>
                    {this.state.novedades.map(nove => 
                        <ItemListNovedad novedad={nove} setNovedad={()=>this.setNovedad}></ItemListNovedad>)
                    }                  
                </div>
            )
        }else{
            return (<ItemNovedad novedad={this.state.novedadAct}></ItemNovedad>)

        }
        
    }
}
