import React, { Component } from 'react'

class Errors extends Component {
    constructor(props){
        super(props);
        this.state={
            errors: props.errors,
        }
        this.mostrarErrores=this.mostrarErrores.bind(this);
    }
    

    render() {
        return (
            this.state.errors.map(er =>
                {
                <p>{er}</p>
    
                })
        )
    }
}
export default Errors;
