import React, { Component } from 'react';
import axios from 'axios';

const eliminar = 'http://localhost:4000/api/libros/eliminar'
const portada = 'http://localhost:4000/uploads/';

export default class UnLibro extends Component {

  render(){
    console.log(this.props)
    //const libro = this.props;
   
    const eliminarLibro = async () => {
        
        alert(this.props.libro.titulo);
        /*
        await axios.post(eliminar ,
            { id: this.props.libro.id },
            { headers: { 'xaccess': this.state.token } }

        ).then(res => {
            alert('Autor eliminado');
            this.getData();
        })
            .catch(err => {
                alert(err)
        });*/
    }
    return (
        
            <div class="card-body">
                <h5 class="card-title"> {this.props.libro.titulo}
                    <form>
                    <button type="burron" className="btn btn-success" onClick={() => {eliminarLibro()}}> X </button>
                    </form>
                </h5>
                
                <h6 class="card-subtitle mb-2 text-muted">ISBN:{this.props.libro.isbn}</h6>
                <h6 class="card-subtitle mb-2 text-muted">Autor: {this.props.libro.autor}</h6>
                <h6 class="card-subtitle mb-2 text-muted">Genero: {this.props.libro.genero}</h6>
                <h6 class="card-subtitle mb-2 text-muted">Editorial: {this.props.libro.editorial}</h6>
                <h6 class="card-subtitle mb-2 text-muted">Fecha de lanzamiento: {this.props.libro.lanzamiento}</h6>
                <h6 class="card-subtitle mb-2 text-muted">Fecha de expiraci√≥n: {this.props.libro.expiracion}</h6>
                <img alt='' width="300px" height="auto" src={portada + `${this.props.libro.portada}`} />
                
            </div>
                  

      



    )
}
}
