import React, { Component } from 'react';
import axios from 'axios';
import { Redirect } from 'react-router-dom';
import Navegacion from './Navegacion';

const cargar = 'http://localhost:4000/api/suscriptores/registrar';

class RegistrarSuscriptor extends Component {
    
    constructor (){
        super();
        this.state = {
            nombre: '', 
            email: '',
            password:'',
            password2:'',
            dni:'',
            suscripcion:'',
            numT:'',
            codT:'',
            token:'',
            user: null,
            errors:[],
           
        };
        this.handleChange = this.handleChange.bind(this);
        this.cargarSuscriptor = this.cargarSuscriptor.bind(this);
        this.onInputChange = this.onInputChange.bind(this);
        this.getToken = this.getToken.bind(this);
        this.getErrors = this.getErrors.bind(this);
        this.setSuscripcion= this.setSuscripcion.bind(this);
        this.setPremium= this.setPremium.bind(this);
        this.setRegular = this.setRegular(this);
    }

    handleChange(event) {
        const { name, value } = event.target;
        this.setState({
            [name]: value
        });
    }    

    onInputChange = (event) => {
        this.setState({
            [event.target.name]: event.target.value
        })
    };
    
    getToken=(res)=>{
            const {data} = res;
            const {token , user} = data; 

            sessionStorage.setItem('token', token );
            sessionStorage.setItem('user', JSON.stringify( {user} ) );
            this.setState(
                {
                token: token,
                user: user,
                }
            );
    };
    getErrors=(err)=>{
      
        const {data} = err;
        
        const {nombre , email, password , password2}=data; 
        
        const error = [nombre , email, password , password2]; 

        
        this.setState(
            {errors:error}
        )
        error.map(err=>{
            if(! err === '') 
                alert(err);
        } );
        
        
    }
    
    setRegular(){
        
        this.setState({
           suscripcion: 'regular'
        });
        
    };

    setPremium(){
     
        this.setState({
            suscripcion: 'premium'
         });
         

    };

    setSuscripcion(){
        if(this.state.suscripcion =='regular' || ''){
            this.setState({
                suscripcion: 'regular'
             });
        }
        else{
            this.setState({
                suscripcion: 'premium'
             });
        }
    }
    
    async cargarSuscriptor(event){
        event.preventDefault();  
        if (this.state.numT.length!=16){
            return alert('Pruebe con una tarjeta que contenga 16 dígitos')
        }
        else if (this.state.codT.length!=3){
            return alert('Ingrese un código de tarjeta de 3 dígitos')
        }
        await axios.post(cargar,{
            nombre:this.state.nombre ,
            email: this.state.email,
            password:this.state.password,
            password2:this.state.password2,
            suscripcion:this.state.suscripcion,
            dni:this.state.dni,
            numT:this.state.numT,
            codT:this.state.codT,
        })
                .then(res => this.getToken(res))
                .catch(res => {
                  this.getErrors(res.response);
                  alert(res.response)
                });

    }

    render(){
        return (
           

        !this.state.token && !this.state.user?

        <div>  
            <script src="https://ajax.googleapis.com/ajax/libs/jquery/1.7.2/jquery.min.js"></script>
            <Navegacion/>
        <div className="form-novedad" >
        <div className="col-md-6 offset-md-3">
        <div className="card card-body text-light bg-dark">
        
        <form onSubmit={this.cargarSuscriptor} >
           

            
            <div className="form-group">
                <label className="text-light">Nombre
                </label>
                <input 
                    className="form-control" 
                    id="nombre" 
                    name ="nombre"
                    onChange={this.onInputChange}
                    value={this.state.nombre}
                    placeholder="Ingrese su nombre"
                    required>
                </input>    
            </div>
            
            <div className="form-group">
                <label className="text-light">Email
                </label>
                <input 
                    className="form-control" 
                    id="email" 
                    name ="email"
                    onChange={this.onInputChange}
                    value={this.state.email}
                    required
                    placeholder="Ingrese un email">
                </input>    
            </div>

            <div className="form-group">
                <label className="text-light">Contraseña</label>
                <input type='password' className="form-control" 
                    id="password" 
                    name ="password"
                    onChange={this.onInputChange}
                    value={this.state.password}
                    placeholder="Ingrese una contraseña"
                    required
                ></input> 
            </div>

            <div className="form-group">
                <label className="text-light">Confirmación de contraseña</label>
                <input type='password' className="form-control" 
                    id="password2" 
                    name ="password2"
                    onChange={this.onInputChange}
                    value={this.state.password2}
                    required
                    placeholder="Vuelva a ingresar su contraseña"
                ></input> 
            </div>

            <div className="form-group">
                <label className="text-light">DNI
                </label>
                <input 
                    className="form-control" 
                    id="dni" 
                    name ="dni"
                    onChange={this.onInputChange}
                    value={this.state.dni}
                    placeholder="Ingrese su DNI"
                    required>
                </input>    
            </div>
            
            <div class="btn-group btn-group-toggle" data-toggle="buttons">
                        <label className="text-light">Tipo de Suscripción </label>
                        <label> <button type="checkbox" class="btn btn-danger" onClick={this.setRegular} > REGULAR</button>
                        <button type="checkbox" class="btn btn-danger" onClick={this.setPremium} > PREMIUM</button></label>  
                </div>

            

            <div className="form-group">
                <label className="text-light">Número de tarjeta
                </label>
                <input 
                    type="number"
                    className="form-control" 
                    id="numT" 
                    name ="numT"
                    onChange={this.onInputChange}
                    value={this.state.numT}
                    required 
                    placeholder="Ingrese número de tarjeta, deberá contener 16 digitos">
                </input>    
            </div>

            <div className="form-group">
                <label className="text-light">Codigo De Tarjeta
                </label>
                <input 
                    type="number"
                    className="form-control" 
                    id="codT" 
                    name ="codT"
                    onChange={this.onInputChange}
                    value={this.state.codT}
                    required
                   
                    placeholder="Ingrese el codigo de su tajeta, deberá contener 3 dígitos">
                </input>    
            </div>
            <div className="form-group">
                <button  type="submit" className="btn btn-danger btn-lg btn-block"
                        >
                    Suscribirse
                </button>
            </div>
                    
    

         </form>
         </div>
         </div>   
         </div>
         </div> 
         :
         <Redirect
         from="/singup"
         to="/home" />
        )
    }
}

export default RegistrarSuscriptor;
