import React, { Component , useEffect} from 'react'
import { BrowserRouter as Router, link, Redirect, Link } from 'react-router-dom';
import axios from 'axios';
import CargarMetadataLibro from '../VistaAdmin/libros/CargarMetadataLibro';
import ListarNovedades from './ListarNovedades';
import Carrusel from './Carrusel';
const libros = 'http://localhost:4000/api/suscriptores/me';

export default class Home extends Component {

    constructor(){
        super();
        this.state={
            user: JSON.parse(sessionStorage.getItem('user')),
            token: JSON.parse(sessionStorage.getItem('token')),
            libros: null
        };
        this.cerrarSesion= this.cerrarSesion.bind(this);

    }

 
    cerrarSesion=()=>{
        sessionStorage.removeItem('token');
       
       
    };
    async componentDidMount() {
        this.getData();
    }

    getData = async () => {
        const {user} = this.state.user;
        const {token} = this.state.token;
        console.log("user");
        console.log(user);
        console.log("token");
        console.log(this.state.token);

        await axios.get(libros,{
            user: user,
            headers:{'xaccess':this.state.token}  

        })
        .then(res =>console.log(res))
        .catch(err =>{
            console.log("error ");
            console.log(err.response)});

        this.setState({
          
        });
    }
   
    
    render() {
        return (
            !this.state.token == ''? 

                <div>
                     <h1>home suscriptor</h1>
                    <form onSubmit= {this.cerrarSesion}>
                        <button type= 'submit'> cerrar sesion </button>
                    </form>
                    <Carrusel></Carrusel>
                    <ListarNovedades></ListarNovedades>
                    <CargarMetadataLibro></CargarMetadataLibro>

                    
                </div>
            :
            <Redirect to="/login"/>
        )
    }
}
