import React, { Component } from 'react';
import axios from 'axios';
import VisualizarUnLibro from './VisualizarUnLibro';
//Constante a la cual hacemos la consulta
const libros = 'http://localhost:4000/api/libros';


export default class ListarVertical extends Component {
    constructor() {
        super();
        this.state = {
            token: sessionStorage.getItem('token'),
            libros: []
        }
       

    }

    async getData(){
        await axios.get(libros, {
            headers: { 'xaccess': this.state.token }
        })
            .then(res => {
                this.setLibros(res.data)
            })
            .catch(err => {
                
                console.log(err)
            });

    }

    

async componentDidMount(){
    this.getData();   
}

setLibros(res) {
    this.setState({
        libros: res
    });
}

render() {
    return (
        <div>
            <div class="card col-md-6 offset-md-3 text-light bg-dark" >
                <h5 class="card-title">Libros: </h5>
                    {this.state.libros.map(libro => <VisualizarUnLibro libro={libro} token={this.state.token} getData={this.getData()}/> )}
            </div>
        </div>
    )
}
}
