import React, { Component } from 'react'

class Errors extends Component {
    constructor(props){
        super(props);
        this.state={
            errors: this.props.errors,
        }
       
    }
    

    render() {
        return (
            
            this.state.errors.map((er) =>
                <div class="alert alert-dark" role="alert">
                {er}
                </div>
            
            )
        )
    }
}
export default Errors;
