import React, { Component } from 'react';
import axios from 'axios';

//Constante a la cual hacemos la consulta
const libros = 'http://localhost:4000/api/libros';
const eliminar = 'http://localhost:4000/api/libros/eliminar'
const portada = 'http://localhost:4000/uploads/';

export default class ListarVertical extends Component {
    constructor() {
        super();
        this.state = {
            token: sessionStorage.getItem('token'),
            libros: []
        }
        this.eliminarLibro = this.eliminarLibro.bind(this);

    }

    async getData(){
        await axios.get(libros, {
            headers: { 'xaccess': this.state.token }
        })
            .then(res => {
                this.setLibros(res.data)
            })
            .catch(err => {
                
                console.log(err)
            });

    }

    

async componentDidMount(){

    this.getData();
    
}
    setLibros(res) {
        
        this.setState({
            libros: res
        });
    }

    

async eliminarLibro(libro){


    await axios.get(eliminar,
        { id: libro.id },
        { headers: { 'xaccess': this.state.token }})
        .then( this.getData())
        .catch(err => {
            
            console.log(err)
        });


}

render() {
    return (
        <div>
            <div class="card col-md-6 offset-md-3 text-light bg-dark" >
                <h5 class="card-title">Libros: </h5>
                {this.state.libros.map(libro =>
                
                    <div class="card-body">
                        <h5 class="card-title"> {libros.titulo} 
                            
                            </h5>
                        <h6 class="card-subtitle mb-2 text-muted">ISBN:{libro.isbn}</h6>
                        <h6 class="card-subtitle mb-2 text-muted">Autor: {libro.autor}</h6>
                        <h6 class="card-subtitle mb-2 text-muted">Genero: {libro.genero}</h6>
                        <h6 class="card-subtitle mb-2 text-muted">Editorial: {libro.editorial}</h6>
                        <h6 class="card-subtitle mb-2 text-muted">Fecha de lanzamiento: {libro.lanzamiento}</h6>
                        <h6 class="card-subtitle mb-2 text-muted">Fecha de expiraci√≥n: {libro.expiracion}</h6>
                        <img alt='' width="300px" height="auto" src={portada + `${libro.portada}`} />






                    </div>
                )}
            </div>


        </div>
    )
}
}
