import React, { Component } from 'react'

class Errors extends Component {
    constructor(props){
        super(props);
        this.state={
            errors: props.errors,
        }
        this.mostrarErrores=this.mostrarErrores.bind(this);
    }
    mostrarErrores=()=>{

        const err = this.state.errors;
        err.map(err => {
            <p> {err} </p>
        })
    } 

    render() {
        return (
            !this.state.errors == null?
            <div>
                {this.mostrarErrores}
            </div>
            :<div></div>
        )
    }
}
export default Errors;
