        return (
            <div>
                <div class="card-body" key={nov.id}>
                </div>
                <div class="card col-md-6 offset-md-3 text-light bg-dark" >
                     <div class="card-body">
                        <img alt='' width="300px" height="auto" src={portada + `${this.props.portada}`} />
                        <h5 class="card-title">Titulo: {nov.titulo}</h5>
                        <h6 class="card-subtitle mb-2 text-muted">Descripcion: {this.props.descripcion}</h6>
                        <h6 class="card-subtitle mb-2 text-muted">Fecha de publicacion: {this.props.publicacion}</h6>
                     </div>
                </div>
            </div>
        )
    }
}

export default  ItemNovedad ;
