import React, { Component } from 'react';

const eliminar = 'http://localhost:4000/api/libros/eliminar'
const portada = 'http://localhost:4000/uploads/';

export default class UnLibro extends Component {

  render(){
    return (
        <form onSubmit= {() => {this.eliminarLibro}}>
                    <div class="card-body">
                        <h5 class="card-title"> {this.state.libro.titulo}
                            <form>
                                <button type="button" className="btn btn-success"  > X </button>
                            </form>
                        </h5>
                        
                        <h6 class="card-subtitle mb-2 text-muted">ISBN:{this.state.libro.isbn}</h6>
                        <h6 class="card-subtitle mb-2 text-muted">Autor: {this.state.libro.autor}</h6>
                        <h6 class="card-subtitle mb-2 text-muted">Genero: {this.state.libro.genero}</h6>
                        <h6 class="card-subtitle mb-2 text-muted">Editorial: {this.state.libro.editorial}</h6>
                        <h6 class="card-subtitle mb-2 text-muted">Fecha de lanzamiento: {this.state.libro.lanzamiento}</h6>
                        <h6 class="card-subtitle mb-2 text-muted">Fecha de expiraci√≥n: {this.state.libro.expiracion}</h6>
                        <img alt='' width="300px" height="auto" src={portada + `${this.state.libro.portada}`} />
                        
                    </div>
                  

                    </form>



    )
}
}
