import React, { Component } from 'react';

const portada = 'http://localhost:4000/uploads/';

class ItemNovedad extends Component {
    constructor (props){
        super(props)


    }

    click=(e)=>{
        this.props.setNovedad(this.props.novedad);
    }
    render() {

        return (
            <div>
                <div class="card-body" >
                </div>
                <div class="card col-md-6 offset-md-3 text-light bg-dark" >
                     <div class="card-body">
                        <h5 onClick={this.click} class="card-title">Titulo: {this.props.novedad.titulo}</h5>
                        <h6 class="card-subtitle mb-2 text-muted">Fecha de publicacion: {this.props.novedad.publicacion}</h6>
                     </div>
                </div>
            </div>
        )
    }
}

export default  ItemNovedad ;
